import React, { useState } from "react";
import "./Dashboard.css";

function Dashboard() {
  const [activeSection, setActiveSection] = useState("damage");

  const renderContent = () => {
    if (activeSection === "damage") {
        return (
          <div className="content-card">
            <h2>Post-Disaster Damage Assessment</h2>
            <p>Upload an image (JPEG, PNG) to assess the severity of damage.</p>
            <div className="file-upload-section">
              <input
                type="file"
                accept="image/jpeg, image/png"
                className="file-input"
              />
              <button className="action-button">Assess</button>
            </div>
            <div className="result-section">
              <h3>Assessment Result:</h3>
              <p>No damage detected.</p> {/* Placeholder for results */}
            </div>
          </div>
        );
      }
      
       else if (activeSection === "resources") {
      return (
        <div className="content-card">
          <h2>Resource Allocation</h2>
          <p>Select the damage level to allocate resources.</p>
          <select className="dropdown">
            <option value="minor">Minor Damage</option>
            <option value="major">Major Damage</option>
            <option value="total">Total Destruction</option>
          </select>
          <button className="action-button">Allocate</button>
          <div className="resource-cards">
            <div className="resource-card">Wooden Planks</div>
            <div className="resource-card">Bricks</div>
            <div className="resource-card">Cement</div>
          </div>
        </div>
      );
    }
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2 className="sidebar-title">Dashboard</h2>
        <nav className="menu">
          <button
            className={`menu-item ${activeSection === "damage" ? "active" : ""}`}
            onClick={() => setActiveSection("damage")}
          >
            Damage Assessment
          </button>
          <button
            className={`menu-item ${activeSection === "resources" ? "active" : ""}`}
            onClick={() => setActiveSection("resources")}
          >
            Resource Allocation
          </button>
        </nav>
      </aside>
      <main className="main-content">{renderContent()}</main>
    </div>
  );
}

export default Dashboard;
