import React, { useState } from 'react';
import './HeroSection.css';
import heroImage from '../assets/images/hero-image.jpg';

function HeroSection() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    const { clientX, clientY } = e;
    setMousePosition({
      x: clientX - window.innerWidth / 2, // Center the movement
      y: clientY - window.innerHeight / 2, // Center the movement
    });
  };

  const calculateTransform = () => {
    const xOffset = mousePosition.x * 0.03; // Control movement sensitivity
    const yOffset = mousePosition.y * 0.03; // Control movement sensitivity
    return `translate(${xOffset}px, ${yOffset}px)`; // Move the card with the pointer
  };

  return (
    <section className="hero" onMouseMove={handleMouseMove}>
      <div className="hero-content">
        <h1>Empowering Communities Through Effective Disaster Response</h1>
        <p>
          At Rescueplex, we harness cutting-edge technology to assess disaster
          damage swiftly and accurately. Join us in making a difference by utilizing
          our platform to ensure timely resource allocation.
        </p>
        <div className="hero-buttons">
          <button className="get-started">Get Started</button>
          <button className="learn-more">Learn More</button>
        </div>
      </div>
      <div
        className="hero-image"
        style={{ transform: calculateTransform() }} // Apply movement to the card
      >
        <img src={heroImage} alt="Disaster Recovery" />
      </div>
    </section>
  );
}

export default HeroSection;
