import React, { useState } from "react";
import "./Dashboard.css";

function Dashboard() {
  const [activeSection, setActiveSection] = useState("damage");
  const [selectedDamage, setSelectedDamage] = useState("");
  const [allocatedResources, setAllocatedResources] = useState([]);

  // Example available resources data
  const availableResources = [
    { name: "Wooden Planks", percentage: 75 },
    { name: "Bricks", percentage: 50 },
    { name: "Cement", percentage: 20 },
  ];

  // Handle allocation logic
  const handleAllocate = () => {
    let resources = [];
    if (selectedDamage === "minor") {
      resources = ["Wooden Planks", "Bricks"];
    } else if (selectedDamage === "major") {
      resources = ["Bricks", "Cement"];
    } else if (selectedDamage === "total") {
      resources = ["Wooden Planks", "Bricks", "Cement"];
    }
    setAllocatedResources(resources);
  };

  const renderContent = () => {
    if (activeSection === "damage") {
      return (
        <div className="content-card">
          <h2>Post-Disaster Damage Assessment</h2>
          <p>Upload an image (JPEG, PNG) to assess the severity of damage.</p>
          <div className="file-upload-section">
            <input
              type="file"
              accept="image/jpeg, image/png"
              className="file-input"
            />
            <button className="action-button">Assess</button>
          </div>
          <div className="result-section">
            <h3>Assessment Result:</h3>
            <p>No damage detected.</p> {/* Placeholder for results */}
          </div>
        </div>
      );
    } else if (activeSection === "resources") {
      return (
        <>
          {/* Resource Allocation Section */}
          <div className="content-card">
            <h2>Resource Allocation</h2>
            <p>Select the damage level to allocate resources.</p>
            <select
              className="dropdown"
              value={selectedDamage}
              onChange={(e) => setSelectedDamage(e.target.value)}
            >
              <option value="">Select Damage Level</option>
              <option value="minor">Minor Damage</option>
              <option value="major">Major Damage</option>
              <option value="total">Total Destruction</option>
            </select>
            <button className="action-button" onClick={handleAllocate}>
              Allocate
            </button>
            <div className="allocated-resources">
              <h3>Allocated Resources:</h3>
              {allocatedResources.length > 0 ? (
                allocatedResources.map((resource, index) => (
                  <div key={index} className="resource-item">
                    {resource}
                  </div>
                ))
              ) : (
                <p>No resources allocated yet.</p>
              )}
            </div>
          </div>

          {/* Available Resources Section */}
          <div className="available-resources">
            <h3>Available Resources</h3>
            <div className="resource-cards">
              {availableResources.map((resource, index) => (
                <div key={index} className="resource-card">
                  <p>{resource.name}</p>
                  <div className="progress-bar-container">
                    <div
                      className="progress-bar"
                      style={{ width: `${resource.percentage}%` }}
                    ></div>
                  </div>
                  <p>{resource.percentage}% Available</p>
                </div>
              ))}
            </div>
          </div>
        </>
      );
    }
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2 className="sidebar-title">Dashboard</h2>
        <nav className="menu">
          <button
            className={`menu-item ${activeSection === "damage" ? "active" : ""}`}
            onClick={() => setActiveSection("damage")}
          >
            Damage Assessment
          </button>
          <button
            className={`menu-item ${activeSection === "resources" ? "active" : ""}`}
            onClick={() => setActiveSection("resources")}
          >
            Resource Allocation
          </button>
        </nav>
      </aside>
      <main className="main-content">{renderContent()}</main>
    </div>
  );
}

export default Dashboard;
