import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function DamageChart({ result }) {
  const data = {
    labels: [
      "No Damage",
      "Minor Damage",
      "Major Damage",
      "Completely Destroyed",
    ],
    datasets: [
      {
        label: "Damage Distribution",
        data: [
          result.building_no_damage,
          result.building_minor_damage,
          result.building_major_damage,
          result.building_complete_destruction,
        ],
        backgroundColor: [
          "#4CAF50",
          "#FFC107",
          "#FF9800",
          "#F44336",
        ],
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { display: false },
      title: {
        display: true,
        text: "Damage Assessment Summary",
      },
    },
  };

  return <Bar data={data} options={options} />;
}

export default DamageChart;
