import React, { useState } from "react";
import "./DamageAssessment.css";

function DamageAssessment() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setResult(null); // reset result on new file selection
  };

  const handleAssessClick = async () => {
    if (!selectedFile) {
      alert("Please upload an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("image", selectedFile);
    setLoading(true);

    try {
      const response = await fetch("http://localhost:5000/analyze-damage", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Failed to get assessment.");
      }

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Assessment Error:", error);
      setResult({ error: "Error processing image. Please try again." });
    } finally {
      setLoading(false);
    }
  };

  const buildingDamageKeys = [
    "building_no_damage",
    "building_minor_damage",
    "building_major_damage",
    "building_complete_destruction"
  ];

  const readableLabel = (key) => {
    return key
      .replace("building_", "")
      .replace(/_/g, " ")
      .replace(/\b\w/g, (char) => char.toUpperCase());
  };

  return (
    <div className="content-card">
      <h2>Post-Disaster Damage Assessment</h2>
      <p>Upload an image (JPG, PNG) to assess the severity of damage.</p>
      <div className="file-upload-section">
        <input
          type="file"
          accept="image/jpeg, image/jpg"
          className="file-input"
          onChange={handleFileChange}
        />
        <button className="action-button" onClick={handleAssessClick}>
          {loading ? "Assessing..." : "Assess"}
        </button>
      </div>

      {result && (
        <div className="result-section">
          <h3>Assessment Result:</h3>
          {result.error ? (
            <p className="error-message">{result.error}</p>
          ) : (
            <div style={{ display: "flex", justifyContent: "space-between", width: "100%" }}>
              <div style={{ textAlign: "left" }}>
                <h4 style={{ color: "#fff", marginBottom: "0.5rem" }}>Building Damage</h4>
                {buildingDamageKeys.map((key) => (
                  <div key={key}>
                    {readableLabel(key)}: <strong>{result[key] ?? 0}</strong>
                  </div>
                ))}
              </div>
              <div style={{ textAlign: "left" }}>
                <h4 style={{ color: "#fff", marginBottom: "0.5rem" }}>Other Classes</h4>
                {Object.entries(result)
                  .filter(([key]) => !buildingDamageKeys.includes(key) && key !== "Background")
                  .map(([key, value]) => (
                    <div key={key}>
                      {key.replace(/_/g, " ").replace(/\b\w/g, (c) => c.toUpperCase())}:{" "}
                      <strong>{value}</strong>
                    </div>
                  ))}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default DamageAssessment;
