import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ResourceAllocation.css";

function ResourceAllocation() {
  const [damageInput, setDamageInput] = useState({
    building_no_damage: 0,
    building_minor_damage: 0,
    building_major_damage: 0,
    building_total_destruction: 0,
  });

  const [allocatedResources, setAllocatedResources] = useState([]);
  const [availableResources, setAvailableResources] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch available resources on component mount
  useEffect(() => {
    fetchAvailableResources();
  }, []);

  const fetchAvailableResources = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/available-resources");
      setAvailableResources(response.data);
    } catch (error) {
      console.error("Error fetching available resources:", error);
    }
  };

  const handleAllocate = async () => {
    setLoading(true);
    try {
      const response = await axios.post("http://127.0.0.1:5000/allocate-resources", damageInput);
      setAllocatedResources(response.data.allocated_resources);
      setAvailableResources(response.data.updated_resources); // Update available resources dynamically
    } catch (error) {
      console.error("Error allocating resources:", error);
    }
    setLoading(false);
  };

  return (
    <div className="resource-allocation-container">
      {/* Resource Allocation Card */}
      <div className="resource-allocation-card">
        <h2>Resource Allocation</h2>
        <p>Enter the number of buildings affected in each category.</p>
        <div className="input-group">
          <label>No Damage:</label>
          <input
            type="number"
            value={damageInput.building_no_damage}
            onChange={(e) => setDamageInput({ ...damageInput, building_no_damage: parseInt(e.target.value) || 0 })}
          />
        </div>
        <div className="input-group">
          <label>Minor Damage:</label>
          <input
            type="number"
            value={damageInput.building_minor_damage}
            onChange={(e) => setDamageInput({ ...damageInput, building_minor_damage: parseInt(e.target.value) || 0 })}
          />
        </div>
        <div className="input-group">
          <label>Major Damage:</label>
          <input
            type="number"
            value={damageInput.building_major_damage}
            onChange={(e) => setDamageInput({ ...damageInput, building_major_damage: parseInt(e.target.value) || 0 })}
          />
        </div>
        <div className="input-group">
          <label>Total Destruction:</label>
          <input
            type="number"
            value={damageInput.building_total_destruction}
            onChange={(e) => setDamageInput({ ...damageInput, building_total_destruction: parseInt(e.target.value) || 0 })}
          />
        </div>
        <button className="action-button" onClick={handleAllocate} disabled={loading}>
          {loading ? "Allocating..." : "Allocate"}
        </button>
        <div className="result-section">
          <h3>Allocated Resources:</h3>
          {allocatedResources.length === 0 ? (
            <p>No resources allocated yet.</p>
          ) : (
            allocatedResources.map((resource, index) => (
              <p key={index}>{resource.resource_name}: {resource.allocated_quantity} units</p>
            ))
          )}
        </div>
      </div>

      {/* Available Resources Section */}
      <div className="resource-available-section">
        <h3>Available Resources</h3>
        <div className="available-resources">
          {availableResources.length === 0 ? (
            <p>Loading available resources...</p>
          ) : (
            availableResources.map((resource, index) => (
              <div className="resource-card" key={index}>
                <p>{resource.resource_name}</p>
                <div className="progress-bar">
                  <div className="progress" style={{ width: `${(resource.quantity / 10000) * 100}%` }}></div>
                </div>
                <p className="progress-text">{resource.quantity} units available</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default ResourceAllocation;
